import React from "react";
import axios, * as others from "https://cdn.skypack.dev/axios@0.21.1";
import ReactPaginate from "https://cdn.skypack.dev/react-paginate@7.1.0";
import { Link } from 'react-router-dom';
import Banner from './Banner';

const { useState, useEffect } = React;

// Main component -------------------------------
const PageComp = (props) => {
  
  const getPost = (page = 1) => {
    axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=2a75f43539036630c5e2f12e4ed0b677?_page=${page}&_limit=10`)
    .then(response => {
      // console.clear();
      console.log(response.data);
      setPosts(response.data);
      setLoading(false);
    });
  }
  
  const [count, setCount] = useState(0);
  const [posts, setPosts] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    getPost();
  }, []);
  
  
  const handlePageClick = (e) => {
    console.log(e.selected);
    setLoading(true);
    getPost(e.selected + 1);
  }
  
  const genPosts = () => {
    const allPost = [];
    posts.map(post => {
      allPost.push(<Post {...post} />);
    });
    
    return allPost;
  }
  
  const allPost = genPosts();
  
  return (
    <div className='main'>
      <h1 className='main-head'>All posts</h1>
      
      <div className='posts'>
        { loading ? <Loader /> : allPost }
      </div>
      
      <div className='paginate'>
        <ReactPaginate 
          previousLabel={"Back"}
          nextLabel={"Next"}
          breakLabel={"..."}
          breakClassName={"break-me"}
          pageCount={100}
          marginPagesDisplayed={5}
          pageRangeDisplayed={5}
          onPageChange={handlePageClick}
          containerClassName={"pagination"}
          subContainerClassName={"pages pagination"}
          activeClassName={"active"}
          />
      </div>
    </div>
  )
}



const Post = props => {
  
  return (
    <div className='bg-dark movie-comp'>
<div className='container'>
<div className='title pb-3 mb-0 pt-5'>
<h3 className='text-white'>Trending</h3>
</div>
<div className='row'>

{
    movies && movies.length > 0 && movies.map((data,moviesData)=>(
      <div className='col-12 col-sm-12 col-md-6 col-lg-4 col-xl-3 col-xl-3 m-auto '>
      <Link to={`/movies/${data.id}`}>
      
      <div className='card bg-dark rounded'  >
      <img src={"https://image.tmdb.org/t/p/w500/" + data.backdrop_path} alt={data.title} className='img-fluid rounded' />
      <div className='card-body '>
        <p className='card-title text-white'>{data.title}</p>
        <div className='row'>
          <span className='rating'>{data.vote_average} / 10</span>
        </div>
      </div>
    </div>
      </Link>
      </div>
     ))
 }
 < div className='buttons'>
 < button className='btn btn-primary'
 onClick={()=>{
  if(pageNumber > 1){
    setPageNumber(pageNumber -1)
  }
 }}
 >Previous</button>
<span className='text-white ms-3'> {pageNumber}</span>
 < button className='btn btn-primary me-3 ms-3'
 onClick={()=>{
  if(pageNumber < 20){
    setPageNumber(pageNumber+1)
  }
 }}
 >next</button>
 </div>
 </div>

</div>

</div>
  )
}


// Loader component -------------------------------
const Loader = props => {
  
  return (
     <div className='loader'>
      Loading...
      <i class="fas fa-sync-alt fa-spin"></i>
    </div>
  )
}
export default PageComp;
