import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom';
import Banner from './Banner';
 import '../App.css'

const MoviesHome = () => {
    
    const [movies , setMovies] = useState([]);
    const [pageNumber , setPageNumber] = useState(1)
  
    useEffect(()=>{
       const getData = async()=>{
        const response = await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=2a75f43539036630c5e2f12e4ed0b677&page=${pageNumber}`)
        // console.log(response);
        const {data} = response;
        console.log(data)
        setMovies(data.results)
       }
       getData(pageNumber);
    },[pageNumber])


// implementing Search Functionality;
const [seachMovies , setSearchMOvies] = useState([])
const [query,setQuery] = useState('')
  const searchMovies = async(e)=>{
    e.preventDefault();
    console.log("Searching....");
   try{
    const url = `https://api.themoviedb.org/3/search/movie?api_key=2a75f43539036630c5e2f12e4ed0b677&query=${query}`;
    const res = await fetch(url);
    const data = await res.json();
    setMovies(data.results)
    console.log(data.results)
   }catch{
    console.log(e)
   }
   
    }
    const handleChange = (e)=>{
      setQuery(e.target.value)
     }

// ----------------------------


  return (
    <div>
   <Banner / >
<div className='bg-dark movie-comp'>
<div className='container'>
<div className='title pb-3 mb-0 pt-5'>
<h3 className='text-white'>Trending</h3>
</div>
<div className='row'>

{
    movies && movies.length > 0 && movies.map((data)=>(
      <div className='col-12 col-sm-12 col-md-6 col-lg-4 col-xl-3 col-xl-3 m-auto ' key={data.id}>
      <Link to={`/movies/${data.id}`}>
      
      <div className='card bg-dark rounded'  >
      <img src={"https://image.tmdb.org/t/p/w500/" + data.backdrop_path} alt={data.title} className='img-fluid rounded' />
      <div className='card-body '>
        <p className='card-title text-white'>{data.title}</p>
        <div className='row'>
          <span className='rating'>{data.vote_average} / 10</span>
        </div>
      </div>
    </div>
      </Link>
      </div>
     ))
 }
 <form className="d-flex" onSubmit={searchMovies}>
 <input className="form-control me-2 w-100" type="search" placeholder="Search movies" aria-label="Search" 

 name='query'
 value={query}
 onChange={handleChange}
 />
 <button className="btn btn-outline-primary"  type="submit">Search</button>
</form>

<div className='container'>
<div className='row'>
{
 movies&& movies.length>0 && movies.map((moviesData)=>(
   <div className='col-12 col-sm-12 col-md-6 col-lg-4 col-xl-3 col-xl-3 m-auto '>
   <Link to={`/movies/${moviesData.id}`}>
   
   <div className='card bg-dark rounded'  >
   <img src={"https://image.tmdb.org/t/p/w500/" + moviesData.poster_path} alt={moviesData.title} className='img-fluid rounded' />
   <div className='card-body '>
     <p className='card-title text-white'>{moviesData.title}</p>
     <div className='row'>
       <span className='rating'>{moviesData.vote_average} / 10</span>
     </div>
   </div>
 </div>
   </Link>
   </div>
 ))
}
</div>
</div>

 < div className='buttons text-center'>
 < button className='btn btn-primary'
 onClick={()=>{
  if(pageNumber > 1){
    setPageNumber(pageNumber -1)
  }
 }}
 >Previous</button>
<span className='text-white ms-3'> {pageNumber}</span>
 < button className='btn btn-primary me-3 ms-3'
 onClick={()=>{
  if(pageNumber <= 100){
    setPageNumber(pageNumber+1)
  }
 }}
 >next</button>
 </div>
 </div>

</div>

</div>
    </div>
  )
}

export default MoviesHome
