import React ,{useState}from 'react';
import { Link } from 'react-router-dom';
import Movies from './Movies';

const MovieSearch = () => {
    const [movies , setMovies] = useState([])
const [query,setQuery] = useState('')
  const searchMovies = async(e)=>{
    e.preventDefault();
    console.log("Searching....");
   try{
    const url = `https://api.themoviedb.org/3/search/movie?api_key=2a75f43539036630c5e2f12e4ed0b677&query=${query}`;
    const res = await fetch(url);
    const data = await res.json();
    setMovies(data.results)
    console.log(data.results)
   }catch{
    console.log(e)
   }
   
    }
    const handleChange = (e)=>{
      setQuery(e.target.value)
     }
  return (
    <>
    <form className="d-flex" onSubmit={searchMovies}>
    <input className="form-control me-2 w-100" type="search" placeholder="Search movies" aria-label="Search" 

    name='query'
    value={query}
    onChange={handleChange}
    />
    <button className="btn btn-outline-primary" onChange={handleChange} type="submit">Search</button>
  </form>
  
  <div className='container'>
  <div className='row'>
  {
    movies&& movies.length>0 && movies.map((moviesData)=>(
     < Movies {...moviesData} / >
    ))
  }
  </div>
  </div>

    </>
  )
}

export default MovieSearch
