import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom';
import '../App.css'


const Movie = () => {
    const [movie, setMovie] = useState({});
    const { id } = useParams()

    useEffect(() => {
        const getMovie = async () => {
            const response = await axios.get(`https://api.themoviedb.org/3/movie/${id}?api_key=2a75f43539036630c5e2f12e4ed0b677`)
            console.log(response);
            const { data } = response;
            console.log(data)
            setMovie(data)
        }
        getMovie();
    }, [])

    return (
        <>

        <div className='movie-details w-100'>
        {
            Object.keys(movie).length > 0 ? (
                <>
                <div className='row'>
                                <div className='movie-img'>
                                    <img src={"https://image.tmdb.org/t/p/w500/" + movie.backdrop_path} alt={movie.title} className='img-fluid rounded d-block w-100'  />

                                    < div className='movie-body-text w-25'>
                                        <h1>{movie.title}</h1>
                                        <p className='lead fw-bolder'>Rating : {movie.vote_average} / 10</p>
                                        <strong className='w-25'>{movie.overview}</strong>
                                        <p className='lead '>Release Date : {movie.release_date} / 10</p>
                                        <span className='lead'>{movie.tagline}</span>
                                    </div>
                                </div>
                                </div>

                            </>
                        ) : null
                    }
                    <>
                    </>
            </div>
        </>
    )
}

export default Movie
