import React from 'react'


const API_IMG = "https://image.tmdb.org/t/p/w500/";
const Movies = ({title,poster_path}) => {
  return (
    <>

  < div className='container'>
  <div className='d-flex'>
 <div className='card'>
 <img src={API_IMG+poster_path} alt={title} / >
 </div>
  
  </div>
  </div>
    </>
  )
}

export default Movies
