import React, { useEffect, useState } from 'react'
import Movies from './Movies';
import axios from 'axios'
const  API_URL = "https://api.themoviedb.org/3/movie/popular?api_key=2a75f43539036630c5e2f12e4ed0b677";


const MoviesHome = () => {
    
    const [movies , setMovies] = useState([]);

    useEffect(()=>{
       const getData = async()=>{
        const response = await axios.get(API_URL)
        // console.log(response);
        const {data} = response;
        console.log(data)
        setMovies(data.results)
       }
       getData();
    },[])
  return (
    <div>
<div className='container bg-dark'>

<div className='row'>
{
    movies && movies.length > 0 && movies.map((data)=>(
       <div className='col-12 col-sm-12 col-md-4 col-lg-3 col-xl-3 col-xl-3'>
       <Movies key={data.id} {...data} / >
       </div>
     ))
 }
</div>
</div>
    </div>
  )
}

export default MoviesHome
