import React from 'react'

import '../App.css';

import "../App.css"
const API_IMG = "https://image.tmdb.org/t/p/w500/";
const Movies = ({title,poster_path,vote_average}) => {
  return (
    <>


 <div className='card bg-dark rounded'  >
 <img src={API_IMG+poster_path} alt={title} className='img-fluid rounded' /  >
 <div className='card-body '>
 <p className='card-title text-white'>{title}</p>
 <div className='row'>
 <span className='rating'>{vote_average} / 10</span>
 </div>
 </div>
 </div>
  

    </>
  )
}

export default Movies
